#!/bin/bash
cat <(echo combo =) <(./../crawl/crawl-ref/source/crawl -list-combos | grep HuFi | sed -r 's/,/\n/g' | sed -r 's/(Fi|Gl)$/\1.good/g' | sed -r 's/(AK|Be|CK|Sk|Wr)$/\1.bad/' | sed -r 's/(AM|Hu)$/\1.javelins/' | sed -r 's/(^Mf.*)\.good$/\1.trident/' | sed -r 's/(^Mf.*)\.bad$/\1.spear/' | sed -r 's/(^Sp.*)\.good$/\1.rapier/' | sed -r 's/(^Sp.*)\.bad$/\1.shortsword/' | sed -r 's/(^(Og|Ko).*)\.good$/\1.flail/' | sed -r 's/(^(Og|Ko).*)\.bad$/\1.mace/' | sed -r 's/(^(Ha|HE).*)\.good$/\1.longsword/' | sed -r 's/(^(Ha|HE).*)\.bad$/\1.falchion/' | sed -r 's/(^(Fe|Gh|Tr).*)\.(good|bad)$/\1.claws/' | sed -r 's/(^.*)\.good$/\1.waraxe/' | sed -r 's/(^.*)\.bad$/\1.handaxe/' | sed -r 's/^/combo += /') > hyperqwcombo.rc
